#include <stdio.h>
#include <fcntl.h>
#include <time.h>
#include <locale.h>

int main (int argc, char *argv[]) {
  setlocale(LC_ALL,"");
  int f = open ("test_disk",O_CREAT|O_WRONLY|O_TRUNC,0644);
  int i;
  char *buf = ""; // 4096
  clock_t start = clock();
  clock_t diff;
  int n = atoi(argv[1]);
  int m = atoi(argv[2]);
  for (i = 0 ; i < n ; i++){
    write(f, buf, m);
  }
  close(f);
  diff = clock() - start;
  int msec = diff * 1000 / CLOCKS_PER_SEC;
  float speed = ((float)n*m) / ((float)msec) /1000;
  
  printf ("%d MB written in %d.%ds (speed = %.2f MB/s)(block = %d bytes)\n", (n*m)/1000000, msec/1000, msec%1000, speed, m);
  return 0;
}
